INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(0, '0000-00-00', 'The Messenger of Allah (saw) said: Whoever forgets a prayer, let him pray it when he remembers it.', 'Sunan An-Nasai');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(1, '0000-00-00', 'The Prophet said, "All drinks that produce intoxication are Haram (forbidden to drink).', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(2, '0000-00-00', 'Whenever the Prophet got up at night, he used to clean his mouth with Siwak.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(3, '0000-00-00', 'Allah''s Apostle said, "If anyone of you enters a mosque, he should pray two Rakat before sitting."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(4, '0000-00-00', 'Allah''s Apostle said, "Whenever you hear the Adhan, say what the Muadhin is saying.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(5, '0000-00-00', 'The Prophet said, "If supper is served, and Iqama is pronounced one should start with the supper."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(6, '0000-00-00', 'The Prophet used to pray a short prayer (in congregation) but used to offer it in a perfect manner.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(7, '0000-00-00', 'The Prophet said, "Straighten your rows or Allah will alter your faces."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(8, '0000-00-00', 'Allah''s Apostle said, "Whoever does not recite Al-Fatiha in his prayer, his prayer is invalid."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(9, '0000-00-00', 'Allah''s Apostle (p.b.u.h) said, "Anyone of you attending the Friday (prayers) should take a bath."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(10, '0000-00-00', 'Whenever Allah''s Apostle saw the rain, he used to say, "O Allah! Let it be a strong fruitful rain."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(11, '0000-00-00', 'Whenever the Prophet got up for Tahajjud prayer he used to clean his mouth (and teeth) with Siwak.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(12, '0000-00-00', 'The Prophet used to lie down on his right side, after offering two Rakat (Sunna) of the Fajr prayer.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(13, '0000-00-00', 'Allah''s Apostle said, "Offer some of your prayers in your houses and do not make them graves."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(14, '0000-00-00', 'Allah''s Apostle said, "Between my house and the pulpit there is a garden of the gardens of Paradise."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(15, '0000-00-00', 'The Prophet said, "The real patience is at the first stroke of a calamity."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(16, '0000-00-00', 'The Prophet ordered the people to pay Zakat-ul-Fitr before going to the Eid prayer.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(17, '0000-00-00', 'The Prophet said, "O Allah! Bestow on Medina twice the blessings You bestowed on Makkah."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(18, '0000-00-00', 'Allah''s Apostle said, "When Ramadan begins, the gates of Paradise are opened."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(19, '0000-00-00', 'Allah''s Apostle used to practise Itikaf in the last ten days of the month of Ramadan.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(20, '0000-00-00', 'The Prophet (SAW) said, "Az-Zulm (Oppression) will be a darkness on the Day of Resurrection."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(21, '0000-00-00', 'Allah''s Messenger used to accept gifts and used to give something in return.', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(22, '0000-00-00', 'The Prophet (SAW) said, "He who takes back his present is like him who swallows his vomit."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(23, '0000-00-00', 'The Prophet (SAW) said, "Plague is the cause of martyrdom of every Muslim (who dies because of it)."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(24, '0000-00-00', 'The Prophet said, "None of you will have faith till he wishes for his (Muslim) brother what he likes for himself."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(25, '0000-00-00', 'The Prophet said "None of you will have faith till he loves me more than his father, his children and all mankind."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(26, '0000-00-00', 'Allah''s Apostle said, ''Whoever prays the two cool prayers (''Asr and Fajr) will go to Paradise.''', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(27, '0000-00-00', 'The Prophet said, "The prayer in congregation is twenty-five times superior to the prayer offered by person alone."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(28, '0000-00-00', 'The Prophet (saw) said to Abu-Dhar, "Listen and obey (your chief) even if he is an Ethiopian with a head like a raisin."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(29, '0000-00-00', 'Allah''s Apostle said, "You will be rewarded for whatever you spend for Allah''s sake even if it were a morsel which you put in your wife''s mouth."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(30, '0000-00-00', 'The Prophet said, "If anyone of you feels drowsy while praying, he should sleep till he understands what he is saying (reciting)."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(31, '0000-00-00', 'The Prophet said, "Straighten your rows as the straightening of rows is essential for a perfect and correct prayer."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(32, '0000-00-00', 'Allah''s Apostle said, "One prayer in my Mosque is better than one thousand prayers in any other mosque except Al-Masjid-AI-Haram."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(33, '0000-00-00', 'The Prophet (p.b.u.h) said, "The best charity is that which is practiced by a wealthy person. And start giving first to your dependents."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(34, '0000-00-00', 'The Prophet said to me, "Do not withhold your money, (for if you did so) Allah would with-hold His blessings from you."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(35, '0000-00-00', 'Allah''s Apostle said, "There are angels guarding the entrances (or roads) of Medina, neither plague nor Ad-Dajjal will be able to enter it."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(36, '0000-00-00', 'Allah''s Apostle said, "The people will remain on the right path as long as they hasten the breaking of the fast."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(37, '0000-00-00', 'The Prophet said, "A time will come when one will not care how one gains one''s money, legally or illegally."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(38, '0000-00-00', 'Allah''s Apostle said, "May Allah''s mercy be on him who is lenient in his buying, selling, and in demanding back his money."', 'Sahih Bukhari');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(39, '0000-00-00', 'The Apostle of Allah (may peace be upon him) used to remember Allah at all moments.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(40, '0000-00-00', 'Bilal was commanded (by the Holy Prophet) to repeat the phrases twice in Adhan, and once in lqama.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(41, '0000-00-00', 'The Prophet (pbuh) said: Iman has over seventy branches, and modesty is a branch of Iman.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(42, '0000-00-00', 'The Apostle (pbuh) observed: When a man calls his brother an unbeliever, it returns (at least) to one of them.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(43, '0000-00-00', 'The Apostle of Allah observed: The best of'' the deeds or deed is the (observance of) prayer at its proper time and kindness to the parents.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(44, '0000-00-00', 'The Apostle of Allah (pbuh) observed: He who as in his heart the weight of a mustard seed of pride shall not enter Paradise.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(45, '0000-00-00', 'The Messenger of Allah (may peace be upon him) said: The Hour (Resurrection) would not come upon anyone so long as he supplicates Allah.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(46, '0000-00-00', 'The Messenger of Allah (pbuh) loved to start from the right-hand side for performing ablution, for combing (the hair) and wearing the shoes.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(47, '0000-00-00', 'The Messenger of Allah (may peace be upon him) said: When the Muadhin calls to prayer, Satan runs back vehemently.', 'Sahih Muslim');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(48, '0000-00-00', 'The earth has been made for me purifying and as a mosque (place for prayer).', 'Sunan Abu Dawood');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(49, '0000-00-00', 'The supplication made between the adhan and the iqamah is not rejected.', 'Sunan Abu Dawood');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(50, '0000-00-00', 'Let the best among you call the adhan for you, and the Quran-readers act as your imams.', 'Sunan Abu Dawood');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(51, '0000-00-00', 'The Prophet (p.b.u.h) said: The best of you are those whose shoulders are soft in prayer.', 'Sunan Abu Dawood');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(52, '0000-00-00', 'The Prophet (p.b.u.h) said: Have the imam in the centre and close up the gaps.', 'Sunan Abu Dawood');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(53, '0000-00-00', 'Your mouths are the paths of the Quran, so perfume them with the tooth stick', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(54, '0000-00-00', 'The Messenger of Allah (saw) used to remember Allah in all circumstances.', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(55, '0000-00-00', 'The Messenger of Allah (saw) used to make his prayer brief but perfect.', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(56, '0000-00-00', 'The Messenger of Allah (saw) said: "Beautify the Quran with your voices."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(57, '0000-00-00', 'The Messenger of Allah (saw) said: ''Patience should come with the first shock."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(58, '0000-00-00', 'The Messenger of Allah (saw) said: "Eat Suhur, for in Suhur there is a blessing."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(59, '0000-00-00', 'The Messenger of Allah (saw) forbade fasting on the Day of Fitr and the Day of Adha.', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(60, '0000-00-00', 'The Prophet (saw) said: "A grateful eater is equal to a patient fasting person."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(61, '0000-00-00', 'The Messenger of Allah (saw) said: "The best of you are those who are best to their womenfolk."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(62, '0000-00-00', 'The Messenger of Allah (saw) said: "The most hated of permissible things to Allah is divorce."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(63, '0000-00-00', 'The Messenger of Allah (saw) said: ''Do not take oaths by idols nor by your forefathers."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(64, '0000-00-00', 'The Messenger of Allah (saw) said: "An oath (leads to) either sin or regret."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(65, '0000-00-00', 'The Messenger of Allah (saw) said: "Your oath is as your companion understands it to be."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(66, '0000-00-00', 'The Messenger of Allah (saw) said: "Whoever achieves at something, let him stick with It."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(67, '0000-00-00', 'The Messenger of Allah (saw) said: "No one hoards but a sinner."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(68, '0000-00-00', 'The Messenger of Allah (saw) said: "When you weigh, allow more."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(69, '0000-00-00', 'The Prophet (saw) said: "O Allah, bless my nation in their early mornings."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(70, '0000-00-00', 'The Messenger of Allah (saw) said: "Do not take back your charity."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(71, '0000-00-00', 'The Messenger of Allah (saw) said: "Give the worker his wages before his sweat dries."', 'Sunan Ibn Majah');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(72, '0000-00-00', 'The Messenger of Allah (saw) said: ''Do Wudu properly.''', 'Sunan An-Nasai');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(73, '0000-00-00', 'The Messenger of Allah (saw) said: Whoever is in the Masjid waiting for the prayer, he is in a state of prayer.', 'Sunan An-Nasai');
INSERT INTO `hadith_of_the_day` (`id`, `hadith_date`, `hadith_text`, `source_reference`) VALUES(74, '0000-00-00', 'The Prophet (saw) said: One of the portents of the Hour will be that people will show off in building Masjids.', 'Sunan An-Nasai');
INSERT INTO `message_board` (`id`, `enable`, `start_date`, `end_date`, `heading`, `arabic`, `english`, `footer`) VALUES(1, 1, '2016-04-01 04:00:00', '2016-05-01 04:00:00', 'Heading 01', 'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ\r\nالْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ\r\nالرَّحْمَنِ الرَّحِيمِ\r\nمَالِكِ يَوْمِ الدِّينِ\r\n<b>إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ</b>\r\n<span style="color:green;">اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ</span>\r\nصِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلَا الضَّالِّينَ', 'n the name of Allah, Most Gracious, Most Merciful.\r\nPraise be to Allah, the Cherisher and Sustainer of the worlds;\r\nMost Gracious, Most Merciful;\r\n<span style="color:red;">Master of the Day of Judgment.</span>\r\nThee do we worship, and Thine aid we seek.\r\nShow us the straight way,\r\nThe way of those on whom Thou hast bestowed Thy Grace, those whose (portion) is not wrath, and who go not astray.\r\n', 'Footer01');
